import React, { Component } from 'react';
import AddTask from './AddTask';
import TaskList from './TaskList';
import './App.css';

class App extends Component {

  state = {
    tasks: [
      {
        id: 0,
        text: 'zagrać w wiedmina',
        date: '2019-12-15',
        important: true,
        active: true,
        finishDate: null,
      },
      { id: 1, text: 'zrobić dobry uczynek', date: '2019-11-05', important: false, active: true, finishDate: null },
      { id: 2, text: 'kupić buty', date: '2019-11-10', important: false, active: true, finishDate: null },
      { id: 3, text: 'sprzedać sukienkę', date: '2019-11-25', important: true, active: true, finishDate: null },
    ]
  }

  deleteTask = (id) => {
    const tasks = [...this.state.tasks];
    const index = tasks.findIndex(task => task.id === id); // or tasks.filter(task => task.id !== id)
    tasks.splice(index, 1)

    this.setState({
      tasks
    })
  }

  changeTaskStatus = (id) => {
    const tasks = Array.from(this.state.tasks);
    tasks.forEach(task => {
      if(task.id === id) {
        task.active = false;
        task.finishDate = new Date().getTime()
      }
    })
    this.setState({
      tasks
    })
  }

  render() {
    return (
      <div className="App">
        To do App
        < AddTask />
        < TaskList tasks={this.state.task} delete={this.deleteTask} change={this.changeTaskStatus}/>
      </div>
    )
  }
}



export default App;
